-btns[_ngcontent-%COMP%]\n{\n    background-color: white;\n    padding: 3%;\n}\n.btn-info[_ngcontent-%COMP%]\n{\n    margin-right: 4%;\n    border: none;\n    outline: none;\n    background-color: white;\n    font-size: 20px;\n    font-weight: bolder;\n    cursor: pointer;\n}\n.btn-info[_ngcontent-%COMP%]:active, .btn-info[_ngcontent-%COMP%]:focus, .btn-info[_ngcontent-%COMP%]:hover\n{\n    border: none;\n    outline: none;\n    box-shadow: none;\n    background-color: white;\n}\n.descriptions[_ngcontent-%COMP%]\n{\n    margin-left: 15%;\n    margin-right: 10%;\n    background-color: white;\n}\n.certificate[_ngcontent-%COMP%]\n{\n    opacity: 0.6; \n    border: 1px solid black; \n    font-weight: bolder; \n    margin-left: 22%; \n    cursor: pointer;\n    margin-top: 2%; \n    display: inline-block; \n    background-color: white; \n    padding: 5px 30px 5px 30px;\n}\n#searchtxt[_ngcontent-%COMP%]\n{\n    font-weight: bold;\n    background-color: rgb(238, 236, 236);\n    padding: 1%;\n    margin-top: -5.4%;\n    width: 70%;\n    margin-bottom: 4%;\n}\n#questions[_ngcontent-%COMP%], #notestxt[_ngcontent-%COMP%]\n{\n    font-weight: bold;\n    background-color: rgb(243, 241, 241);\n    padding: 2%;\n    margin-top: 3%;\n    margin-bottom: 8%;\n    width: 70%;\n}\n\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
